    /// <summary>
    /// Data of a player.
    /// </summary>
    [Equals]
    public sealed class Player
    {
        internal Player(
            Guid id,
            double sizeFactor,
            Position position,
            Degrees direction,
            Pen pen,
            Option<SpeechBubble> speechBubble,
            IImmutableList<Costume> costumes,
            int costumeIndex)
        {
            Id = id;
            SizeFactor = sizeFactor;
            Position = position ?? throw new ArgumentNullException(nameof(position));
            Direction = direction ?? throw new ArgumentNullException(nameof(direction));
            Pen = pen ?? throw new ArgumentNullException(nameof(pen));
            SpeechBubble = speechBubble;
            Costumes = costumes ?? throw new ArgumentNullException(nameof(costumes));
            if (Costumes.Count < 1)
            {
                throw new ArgumentException("Player must have at least one costume", nameof(costumes));
            }
            CostumeIndex = costumeIndex;
        }

        internal Guid Id { get; }
        internal double SizeFactor { get; }
        [IgnoreDuringEquals] internal Size Size => Costume.Size * SizeFactor;
        internal Position Position { get; }
        [IgnoreDuringEquals] internal Rectangle Bounds =>
            new Rectangle(
                new Position(Position.X - Size.Width / 2, Position.Y - Size.Height / 2),
                Size);
        internal Degrees Direction { get; }
        internal Pen Pen { get; }
        internal Option<SpeechBubble> SpeechBubble { get; }
        internal IImmutableList<Costume> Costumes { get; }
        internal int CostumeIndex { get; }
        [IgnoreDuringEquals] internal Costume Costume => Costumes[CostumeIndex];

        /// <summary>
        /// Creates a player with a list of costumes.
        /// The player is placed at (0,0), has 0 degrees rotation and the default pen.
        /// </summary>
        /// <param name="costumes">The costumes of the player.</param>
        public static Player Create(IImmutableList<Costume> costumes)
        {
            return new Player(
                Guid.NewGuid(),
                1,
                Position.Zero,
                Degrees.Zero,
                Pen.Default,
                None,
                costumes,
                0);
        }

        /// <summary>
        /// Creates a player with a single costume.
        /// The player is placed at (0,0), has 0 degrees rotation and the default pen.
        /// </summary>
        /// <param name="costume">The costume of the player.</param>
        public static Player Create(Costume costume)
        {
            return Create(ImmutableList.Create(costume));
        }

